insert into posts (author_id, time, title, post_text, update_date, is_blocked, is_deleted) values (16, '2021-10-10 00:31:51', 'Объявление:', 'Авдотья Петровна, 55-летняя женщина, мать троих программистов, просит кого-нибудь не психованного научить ее интернету.', '2021-10-08 12:14:04', false, false);
