insert into posts (author_id, time, title, post_text, update_date, is_blocked, is_deleted) values (32, '2021-10-07 20:42:40', 'Низкоуровневый язык — это...', 'Низкоуровневый язык — это когда требуется внимание к вещам, которые никак не связаны с программами на этом языке.', '2022-06-12 18:23:39', false, false);
insert into posts (author_id, time, title, post_text, update_date, is_blocked, is_deleted) values (23, '2022-08-13 00:33:45', 'Не работает?', 'Не волнуйтесь, если что-то не работает. Если бы всё работало, вас бы уволили.', '2022-01-02 07:45:05', false, false);
